#define rez iResolution.xy
float DE(vec2 z){
  const int iters=200;
  float scl=1.0+(0.5+0.5*cos(iTime*0.1))*500000.;
  if(z.x<0.){z/=scl;scl=1.;}
  z+=vec2(-.74820292,-.18466048)*scl;
  vec2 c=z,mc=fract(c)-0.5;
  float d=step(mc.x*mc.y,0.)*.2+.7;
  float m=dot(z,z);
  int n=0; 
  for (n = 0; n < iters && m<4.*scl*scl; n++) {  
   z=vec2(z.x*z.x-z.y*z.y,2.0*z.x*z.y)/scl+c;
   m = dot(z,z);
  }
  z/=scl;
  m = dot(z,z);
  return (float(iters-n)+1.33*log(log(m)))/float(iters);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv=(2.0*fragCoord.xy-rez)/rez.x;
  float d=DE(2.*uv);
  fragColor=vec4(d,d,d,1.0);
}
