#define PI 3.14159265359

float plot(vec2 st, float pct){
  return  smoothstep(pct-0.01, pct, st.y) -
          smoothstep(pct, pct+0.01, st.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    vec3 pct = vec3(0.0);

    vec3 x_mod = vec3( st.x, st.x-PI/3., st.x-2./3.*PI);
    vec3 freq_mod = vec3(4., 4., 4.);
    vec3 amplitude_mod = vec3(2., 2., 2.);

    pct.r = (sin((abs(iTime) + (x_mod.r)) * freq_mod.r) / amplitude_mod.r + 0.5);
    pct.g = (sin((abs(iTime) + (x_mod.g)) * freq_mod.g) / amplitude_mod.g + 0.5);
    pct.b = (sin((abs(iTime) + (x_mod.b)) * freq_mod.b) / amplitude_mod.b + 0.5);

    color.r = mix(color.r, 1., pct.r);
    color.g = mix(color.g, 1., pct.g);
    color.b = mix(color.b, 1., pct.b);

    // Plot transition lines for each channel
    color.r = mix(color.r, 1.,plot(st,pct.r));
    color.g = mix(color.g, 1.,plot(st,pct.g));
    color.b = mix(color.b, 1.,plot(st,pct.b));

    fragColor = vec4(color,1.0);
}
