// Wrote it for myself to understand gamma-correction better.
// Pressing LMB toggles between linearly-percieved (with gamma correction)
// and linearly-emitted color (without gamma correction).
// Gamma of 2.2 might not work for some monitors. On my it is 1.81. You can check here: https://www.shadertoy.com/view/Xdl3DM
#define BANDS 20.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float adjust = 1.0f / BANDS;
    float bands = (uv.x) * BANDS;
 	float col = float(floor(bands) * adjust);

    float midgrey = 0.5;
    col = mix( midgrey,  col, smoothstep(0.1,0.101,length(uv-vec2(0.5,0.5))) ); 

    if(iMouse.z > 0.0)
    {
        col = pow(col, 1.0 / 2.2);
        midgrey = pow(midgrey, 1.0 / 2.2);
    }    

    fragColor = vec4(vec3(col), 1.0);
}
