vec3 white = vec3(1.0, 1.0, 1.0);
vec3 black = vec3(0.0, 0.0, 0.0);
const float R = 40.;
const float pixelWidth = 1.;
const float thickness = .5;

//
// coord - pixel to test
// center - center of circle
// radius - radius of circle
// tolerance - thickness of circle line
vec3 test(vec2 coord, vec2 center, float radius, float thickness) {
    float dist_to_center = sqrt(pow(coord.x - center.x, 2.) + pow(coord.y - center.y, 2.));
    float delta = dist_to_center-radius;

    vec2 uv = gl_FragCoord.xy/iResolution.xy;
    vec3 inside = .5 + .5*cos(iTime+uv.xyx+vec3(0,2,4));

    float blend = smoothstep(0., pixelWidth, abs(delta) - thickness);
    if (delta == 0.) {
        return black;
    } else if (delta  < 0.) {
        return mix(white, inside, blend);
    } else if (delta  > 0.) {
        return mix(white, black, blend);
    }

}

void mainImage(out vec4 out_color, vec2 fragCoord) {
	vec2 center = iResolution.xy/2.;
    float r;

    if (iMouse.xy == vec2(0) ) r = R; else r = distance(center, iMouse.xy);

    vec3 c = test(gl_FragCoord.xy.xy, center, r, thickness);

    out_color = vec4(c, 1.);
}
