#define t iTime*.5

float lines(vec2 uv){
	uv = uv*50.;
    float freq = smoothstep(-1.,1.,-cos(t*0.5))*0.925+0.075;
    uv.x += sin((floor(uv.y*.5+.5)*2.-1.)*freq + t*2.5)*14.;
    float phase = floor(uv.y*.5+.5)*2.-1.;
    float size = smoothstep(-1.,1.,sin(t*3. +phase*.125))*10.;
    uv.y = fract(uv.y*.5+.5)*2.-1.;
	return smoothstep(0.4,0.6,length(uv - vec2(clamp(uv.x,-size, size), 0)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv = clamp(uv,-.5,.5);
    float d = 1.-min(lines(uv*-1.),lines(uv.yx));
    vec3 col = d*(sin(vec3(1.,.5,.25)*t*5.)*.5+.5);
    fragColor = vec4(col,1.0);
}
