void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 texuv = uv;
    texuv.x /= 3.5;
    //remove previous line for non-nyan cat images
    vec3 img = texture(iChannel0, texuv).xyz;
    float flimg = (img.x + img.y + img.z) / 3.0;
    uv.y /= iResolution.x / iResolution.y;

    float thresh = 0.35;
    //threshold for brightness, higher values make the image darker
    float diam = 0.01;
    //diameter of individual screentone dots
    float dist = distance(uv, uv - mod(uv, diam) + diam/2.0)/diam;
    dist = 1.0 - dist;
    //distance from position to center of current modulus cell inverted
    flimg -= dist/2.0;
    flimg = step(thresh, flimg);
    vec3 color = vec3(0.5, 0.2, 0.8);

    fragColor = vec4(vec3(flimg) * color, 1.0);
}
