//y=k(x-x0)+b
float InLine(vec2 pos,float antiAltas,float k,float x0,float b)
{
    float y = k*(pos.x-x0) + b;
    float dif = abs(y-pos.y);
    return smoothstep(antiAltas,0.0,dif);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
	float width = 0.005;
    fragColor.r=InLine(uv,width,2.0,0.1,0.0);
}
