vec3 rainbow(float h) {
    h = fract(h); 
    float r = abs(h * 6. - 3.) - 1.; 
    float g = 2. - abs(h * 6. - 2.); 
    float b = 2. - abs(h * 6. - 4.); 
    vec3 rgb = vec3(r,g,b); 
     return rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy=fragCoord/iResolution.xy;
   vec3 rainbow_col=rainbow(xy.x);
    //vec3 rainbow_col=rainbow(xy.x-xy.y);
    vec4 texColor=texture(iChannel0,xy);

    fragColor=texColor*vec4(rainbow_col,texColor);
}
