void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float SPEED = pow(iTime/20.0,2.0);

    ivec2 iCoord = ivec2(fragCoord);



   	int red = iCoord.x ^ iCoord.y;
    int green = iCoord.x & iCoord.y;
    int blue = iCoord.x &~ iCoord.y;


    vec3 col = vec3(red,green,blue);

    // Output to screen
    fragColor = vec4(col, 1) * SPEED;
}
