float channel(vec2 uv, float t) {
    vec2 pos = vec2(cos(t*1.02) * .5, sin(t*2.345) * .25);

    return .1/length(uv - pos);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float t = iTime * .5;

    vec3 m = vec3(1. - uv.y -.6);
    m *= .2;

    m = min(m, vec3(.001, .04, .1));

    float r = (channel(uv*.98, t + .015));
    float g = (channel(uv, t));
    float b = (channel(uv*1.02, t - .015));

    m += vec3(r, g, b);

    fragColor = vec4(m, 1.0);
}
