void mainImage( out vec4 O, vec2 u )
{
    vec2 U = ( u+u - R ) / R.y;

    O = T(u);                                                    // load drawing

    for(int i=1; i<N; i++)                                       // draw moving particles
        O.rg = max( O.rg, D( T(vec2(i,0)).xy , 1.5 ) * vec2(.8,.4) );

    O = mix( O, vec4(.5), D( T(0).xy + vec2(0,.1*T(0).z), 4.) ); // draw digger
}

#define H(s) fract(sin(vec2(s,u)*mat2(127.1,311.7, 269.5,183.3)) *4e5 )

void mainImage( out vec4 O, vec2 u )
{
    vec2 U = ( u+u - R ) / R.y, P, Z=R-R;
    if (iFrame<1) {                                           // --- init
        O-=O;
        if (U.y < -.5) O = vec4(1.+U.y,-.5-U.y,0,1);          // draw ground
        if (u.y == .5) O = vec4( -R , Z );                    // particles
        if (u == .5+Z) O = vec4(0,-.45,.5,0);                 // digger
        return;
    }    
    O = T(u);                                                 // restore state

    if ( u == .5+Z )                                          // --- digger
            iMouse.z > 0.
              ? O.xy = ( 2.*iMouse.xy - R ) / R.y, 0.
              : O.y = max(-.96, O.y - .01/60.),               // digger depth
            O.z = .5+.5*atan(9.*sin(6.*iTime))/1.46;          // digger hit rythm. see https://www.shadertoy.com/view/Wsyczz
    else if ( u.y == .5 && int(u.x) < N )                     // --- particle simulation
    {
        if ( ( O.zw==Z || O.y < -1. )                         // particle available
            && H(iTime).x > T(0).z )                          // creation rythm
            O.xy = T(0).xy,                                   // start position
            O.zw = H(-iTime), O.z-=.5, O.w*=1.5;              // init velocity
        P = O.xy;
        O.w  -= .05,                                          // Newton: v += a.dt , a = -g
        O.xy += .05*O.zw;                                     //         p += v.dt
        if (T( (R+R.y*O.xy)*.5 +.5).a > 0.)                   // collision: backtrack + ends particle
            O = vec4( P, 0, O.z != 0. ? -.1 : 0. );           // 1st: fall vertically
    }                                                         // 2nd: ends & recycle
    else {                                                    // --- draw
        for(int i=0; i<N; i++) {
            vec4 p = T(vec2(i,0));
            if (p.zw==Z) O.ra = max(O.ra, D(p.xy, 1.5 )*.6);  // ended particle: drop dirt
        }
        P = ( U - T(0).xy +vec2(0,.05)) * mat2(-1,1,1,1);     // carve digger place
        O =  clamp( O - max(0.,min(P.x,P.y)), 0.,1. );        
    }
 }
