// https://pbs.twimg.com/media/ElcRDa5W0AAglDO?format=jpg

float c(vec2 u, float i){
    float d = length(u),
        k = smoothstep(d,d * 1.005, .5),
        r = 1. - step( max(abs(u.x), abs(u.y)), .5);
    return i > .7 ? r : max(k,r);
}

void mainImage( out vec4 fragColor, in vec2 f )
{
    vec2 res = iResolution.xy,
        uv = f.xy / min(res.x, res.y),
        u = (uv - .5) * 1.1;
    float d = 1.;
    float r = 1. - step( max(abs(u.x), abs(u.y)), .5);   
    for(float i = 0. ; i < 1. ; i += 1. / 5.){
        d = min(d,c(u,i));
        u *= 1.415;
    }
    float m = abs(u.x) < abs(u.y) ? f.x : f.y;
    float l = min(d, max(r,floor(fract(m / 7.) + .6)));
    vec3 front = vec3(.35, .31, .22),
        back = vec3(1., .97, .9);
    fragColor = vec4(mix(front, back, l), 1.);
}
