//Ethan Alexander Shulman 2019

vec4 hash(vec4 p) {
    return fract(p*vec4(23.36834,78.723, 54.59, 99.477)+normalize(fract(p.yxwz*73.91374)+1e-4));
}

void mainImage( out vec4 o, in vec2 u)
{
    #define intensity(c) c.x
    #define ts(o) texture(iChannel0,(u+o)/iResolution.xy)
    if (u.x > iResolution.y) {
     	o = ts(vec2(-1,0));   
    	return;
    }
    if (u.y < 1.) {
        o = hash((sin(iTime*vec4(.00283,.00336,.0019,.007))*.5+.5)*1000.+u.xyyx*vec4(1,1,1.2,1));
    } else {
        float dir = mod(floor(u.x)+mod(floor(u.y),2.),2.)*2.-1.;
        vec4 val = ts(vec2(0,-1)),
            oval = ts(vec2(dir,-1));
        float iv = intensity(val),
            ov = intensity(oval);
        if (dir > 0.) {
            if (ov > iv) o = val;
            else o = oval;
        } else {
            if (ov > iv) o = oval;
            else o = val;
        }
    }
}
