vec3 hash3( vec2 p )
{
    vec3 q = vec3( 127.0, //dot(p,vec2(127.1,311.7)),
                      311.7, //vec2(0.0, 0.0)),
                      dot(p,vec2(219.2,371.9))
                     );
    return fract(q-sin(p.xyx));
}

float tear(vec2 uv, float r, float f, float cp, float rp) {

    float x = uv.y < 0. ? length(uv) : length(vec2(uv.x, pow(uv.y * f, cp)));
    float y = uv.y < 0. ? r : r - pow(uv.y * f, rp);
    return smoothstep(x, x + 8. / iResolution.y, y);
}

float voronoi_leaf( vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

    float k = 63.0;// + pow(x.y-1.0, 2.0);//*pow(1.0,1.0);

    float va = 0.0;
    float wt = 0.0;
    vec3 od = hash3( p )*vec3(1.0,1.0,1.0);
    vec2 g = vec2( float(0),float(0) );     
    vec3 o = od + vec3(g, 0);
    vec2 r = g - f + o.xy;
    float d = dot(r,r);
    float ww = pow( 1.0 - smoothstep(0.0,1.414,d), k*3.0 );
    va += o.z*ww;
    wt += ww;

    return va/wt;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float col = voronoi_leaf(50.0 * uv);
    fragColor = vec4(0.0, col*sin(uv.x*3.0)*cos(uv.y*3.0-1.5), 0.0 ,1.0);
}
