float distanceDrop(float distance)
{
    return smoothstep(10.0,0.0,distance);
}

float timeDrop(float time, float keepTime)
{
    return smoothstep(keepTime, 0.0, time);
}

float wave(vec2 center, vec2 pos, float keepTime, float periodTime, float speed)
{
    float time = mod(iTime, periodTime);
	float dist = length(pos - center);
    float minTime = dist/speed;

    float h = sin(-time*speed + dist - 3.141592654*0.5)*0.5 + 0.5;
    h *= step(0.0,time - minTime);

    h *= distanceDrop(dist);
    h *= timeDrop(time, keepTime);

    // h (0,1)
   	return h;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = 14.0*(2.0*fragCoord - iResolution.xy)/iResolution.y;

    vec2 center = vec2(0.0);

    float h = wave(center, pos, 1.5, 2.0, 14.0);

    // Black and white 
     fragColor = vec4(h);

    // Color
    vec3 waveColor = 
        vec3(0.1,0.15,0.2)*pow(1.5-length(uv-vec2(0.5)),2.0) +
        vec3(0.4,0.7,0.8)*h;
    fragColor = vec4(waveColor,1.0);
}
