void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 color = vec3(0.0);
    vec2 aspectRatio = vec2(iResolution.x/iResolution.y, 1.0);
    vec2 uv = (fragCoord.xy / iResolution.xy - 0.5) * aspectRatio;
    vec2 uv2 = vec2(1.4, 1.3) * ((fragCoord.xy / iResolution.xy - 0.5) * aspectRatio) + vec2(0.0, -0.13);

    if ((abs(uv2.x) < 0.15 && abs(uv2.y) < 0.25) ||
        (uv2.x < -0.1 && uv2.x > -0.4 && uv2.y < -0.0 && uv2.y > -0.5) || 
        (uv2.x > 0.1 && uv2.x < 0.4 && uv2.y < -0.0 && uv2.y > -0.5))
    { 
        color = vec3(1.0);
    }

    color = mix(color * 6.0, color * pow(1.0 - length(uv / aspectRatio), 5.0), 0.999);
    color += 0.1 * max(vec3(sin(fragCoord.y / iResolution.y * 300.0 + iTime * 20.)), 0.4);

    fragColor = vec4(color, 1.0);
}
