void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 offset = vec4(0.0,0.0,0.0,1.0);
    vec4 texColor = texture(iChannel3,xy);
    vec4 Color_1 = texture(iChannel3,vec2(xy.x,xy.y+0.005));
    vec4 Color_2 = texture(iChannel3,vec2(xy.x,xy.y-0.005));
    vec4 Color_3 = texture(iChannel3,vec2(xy.x+0.005,xy.y));
    vec4 Color_4 = texture(iChannel3,vec2(xy.x-0.005,xy.y));
    vec4 Color_5 = texture(iChannel3,vec2(xy.x+0.005,xy.y+0.005));
    vec4 Color_6 = texture(iChannel3,vec2(xy.x+0.005,xy.y-0.005));
    vec4 Color_7 = texture(iChannel3,vec2(xy.x-0.005,xy.y+0.005));
    vec4 Color_8 = texture(iChannel3,vec2(xy.x-0.005,xy.y+0.005));
	fragColor = 6.0*texColor-Color_1-Color_2-Color_3-Color_4-0.5*Color_5-0.5*Color_6-0.5*Color_7-0.5*Color_8+offset;//Set the screen pixel to that color
    float average = (fragColor.r + fragColor.g + fragColor.b)/3.0;
	fragColor = vec4(average,average,average,1.0);
}
