vec4 calc( sampler2D sam, vec2 g, vec2 s )
{
    vec4 left = texture(sam, (g-vec2(-1,0))/s);
	vec4 right = texture(sam, (g-vec2(1,0))/s);
	vec4 top = texture(sam, (g-vec2(0,1))/s);
	vec4 bottom = texture(sam, (g-vec2(0,-1))/s);

	vec4 st = texture(sam, g/s);
	vec4 lst = texture(sam, (g-st.xy)/s);

	vec2 grad = vec2(right.z - left.z, top.z - bottom.z);
	float diff = (left.z + right.z + top.z + bottom.z ) / 4.;
	float div = (left.x - right.x + bottom.y - top.y) / 1000.;

	lst.xy += grad;
	lst.z = diff + div; // the normal formula is - div :)
	lst.w = 1.0;

    if (g.x < 1. || g.x > s.x - 1.) lst.x = 0.;
	if (g.y < 1. || g.y > s.y - 1.) lst.y = 0.;

    return lst;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = calc(iChannel0, fragCoord, iResolution.xy);

    if (iFrame < 1 || iMouse.z > 0.) fragColor = vec4(0);

    float ratio = 0.1;

    fragColor.z = fragColor.z * ratio + (texture(iChannel1, fragCoord / iResolution.xy).z) * (1.-ratio);
}

