float oppai(float x) {
    x = mod(x, 1.0);
    return pow(x * -2.0 + 1.0, 2.0);
}

float redline(vec2 uv,float left ,float right, float bottom) {
    if (uv.x > left && uv.x < right && uv.y > bottom) {
        return 1.0;
    }

    return 0.0;
}

float circle(vec2 uv) {
    if (distance(vec2(0.5, 0.5), uv) > 0.5) {
        return 0.0;
    }
    return 1.0;   
}

float flip(float x, float scale) {
    float bottom = (1.0 - abs(scale)) / 2.0;
    if (scale < 0.0) {
        return x;  
    } else {
        float top = bottom + scale;
        if (top < x || bottom > x) {
            return 0.0;
        }

        return (x - bottom) / scale;//(x) / scale - scale;
    }
}

float superFlip(float x, float scale) {
