#define PI 3.141592

float line(vec2 uv, float offset)
{
    return smoothstep(0.25 * sin(uv.x * 2. * PI), 0.25 * sin(uv.x * 2. * PI) + 0.01, uv.y + offset);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y -= 0.5;
    float width = 0.005;

    float edge = line(uv, 0.);
    float green = (1. - edge) * line(uv, width);


    vec3 color = vec3(0., green, 0.);
    fragColor = vec4(color,1.0);
}
