float Band (float t, float start, float end, float blur) {
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep( end + blur, end - blur,t );
    return step1 * step2;
}

float Rectangle (vec2 uv, float blur, float left, float right, float bottom, float top) {
    float r;
    r = Band(uv.x, left, right, blur);
    r *= Band(uv.y, bottom, top, blur);
    return r;
}

float line (vec2 uv, vec2 p, float len, float thick, float m) {
    float c, x ,y;
    uv -= p;
    x = uv.x;
    y = uv.y;
    y += m*x;
    c += Rectangle( vec2(x,y), 0.001,-len,len,-thick, thick);
    return c;
}

float Square (vec2 uv, float blur, float point) {
    return Rectangle(uv, blur, -point, point, -point, point);
}

float Triangle (vec2 uv, float blur, float point) {
    float t = uv.y;
    return Rectangle( uv, blur, -point + t, point -t ,-point - t , point + t);
}

float ReverseTriangle (vec2 uv, float blur, float point) {
    float t = uv.y*-1.;
    return Rectangle( uv, blur, -point + t, point -t ,-point - t , point + t);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col;
    float x = uv.x;
    float y = uv.y;
    float c, row;

    vec2 pos;

    row = -0.1;
    pos = vec2(.01,row);
    c += Triangle(uv - pos ,.001,.5);

    pos = vec2(.01,row);
    c -= ReverseTriangle(uv - pos ,.001,.25);

    row = -0.225;
    pos = vec2(.385,row);
    c -= ReverseTriangle(uv - pos ,.001,.123);

    pos = vec2(-.365,row);
    c -= ReverseTriangle(uv - pos ,.001,.123);

	row = 0.15;
    pos = vec2(.01,row);
    c -= ReverseTriangle(uv - pos ,.001,.123);


    col = vec3(1.,0.,0.)*c;
    fragColor = vec4(col,1.);
}
