//Improved and shortened by Fabrice Neyret
void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 1.2 * (u - .5*R ) / R.y,
         B = max( 1.2*abs(u/R-.5)-.5,0.);
    U.x = mod(U.x, .6)-.3;  
    U.y = max(-U.y-.2, 0.);
    float l = abs(length(U)-.2)-.03;

    O = vec4( ( max(l,0.) + max(B.x,B.y) ) *R.y/1.5 );
}
