void mainImage (out vec4 _, vec2 p)
{
  _ = sqrt(texture(iChannel0, p/iChannelResolution[0].xy).zzzz);
}

#define coprimes vec2(2,3)
vec2 halton (vec2 s)
{
  vec4 a = vec4(1,1,0,0);
  while (s.x > 0. && s.y > 0.)
  {
    a.xy = a.xy/coprimes;
    a.zw += a.xy*mod(vec2(s),coprimes);
    s = floor(s/coprimes);
  }
  return a.zw;
}

void mainImage (out vec4 _, vec2 p)
{
  float dx = 1./min(iChannelResolution[0].x,iChannelResolution[0].y);
  vec2 uv = (2.*p-iResolution.xy)*dx;
  vec4 a = texture(iChannel0,p/iChannelResolution[0].xy);
  if (a.x != iResolution.x || a.y != iResolution.y)
  {
    a = vec4(iResolution.xy,1,1);
  }
  _ = a;
  _.zw *= smoothstep(dx,2.*dx,distance(.5-uv,halton(vec2(iFrame))));
}
