void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float PI = 3.1415926535897932384626433832795;
    vec2 coord = fragCoord.xy;
    vec2 center = iResolution.xy /2.0;
    float dist = length(center - coord);
    float circlesOut = cos(dist/7.0 - iTime*6.0);
    circlesOut *= 5.0;
    float circlesIn = cos(dist/7.0 + iTime*6.0);
    circlesIn *= 5.0;
    circlesIn = clamp(circlesIn,0.0,1.0);
    circlesOut = clamp(circlesOut,0.0,1.0);
    float edge = clamp(dist-100.0,0.0,1.0);
    circlesOut *= edge;
    circlesIn *= 1.0-edge;

    float dx = coord.x - center.x;
    float dy = coord.y - center.y;

    float c = circlesOut;
    float targetAngle = (mod(iTime*4.0,36.0)*10.0);
    float angle = 180.0 + atan(dy,dx) * 180.0 / PI;
    float a = angle-targetAngle+180.0+ 360.0;
    float da = 360.0 - mod(a,360.0) - 180.0;
    if (da < 30.0 && da > 0.0) {
		fragColor = vec4(0,c,0,0.0);
    } else {
        fragColor = vec4(0,0,0,0.0);
    }
}
