void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float aspect = iResolution.y / iResolution.x;
    ivec2 coord = ivec2((fragCoord.x * aspect),(fragCoord.y));

    int t = iFrame % int(1.2 * iResolution.y);

    vec3 col = texture(iChannel0, uv).rgb;
    vec3 c1 = vec3(0);
    vec3 c2 = vec3(1);

    if (t == 0)
    {
        col = c1;
        if (coord.x == coord.y)
        {
            col = c2;
        }
    }
    else
    {
        int y = (coord.x ^ t);
        if (coord.y == y)
        {
            col = c2;
        }
    }



    // Output to screen
    fragColor = vec4(col,1.0);
}
