void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec2 lastUv = vec2(iMouse.x/iResolution.x,iMouse.y/iResolution.y);

    if(iMouse.w > .9){
    if(uv.x < lastUv.x + .03 && uv.x > lastUv.x - .03)
        if(uv.y < lastUv.y  + .05&& uv.y  > lastUv.y - .05)
        fragColor = vec4(1. - iMouse.x/iResolution.x,iMouse.x/iResolution.x,1. - iMouse.y/iResolution.y,0);
        }


    else
    fragColor = vec4(0,0,0,0);
}
