float rectangle_1(vec2 center, float width, float height, vec2 uv, float blur, vec2 offset){
	center += offset;
    float wLen = abs(uv.x - center.x);
    float hLen = abs(uv.y - center.y);

    float wCol = smoothstep(width, width - blur, wLen);
    float hCol = smoothstep(height, height - blur, hLen);

    return wCol * hCol;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 backGroundColor = vec3(1.0,1.0,0.5);

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 center = vec2(0.0);
    float width = 0.8;
    float height = 0.8;
    float blur = 0.1;
    vec2 offset = vec2(.0);

    float col = rectangle_1(center, width, height, uv, blur, offset);

    fragColor = vec4(backGroundColor * vec3(abs(col)), 1.0);
}
