// antialiased golfed version of https://shadertoy.com/view/XdcGzS

// --- 225 by Fab
//                                                          k-- : not ok on Angle
#define t(k) smoothstep(-3.,3., R*l/exp2(l)*(abs( fract( exp2(ceil(l)k) * atan(U.y,U.x)/6.283 + iTime ) -.5 ) - .25) ) //
void mainImage(out vec4 O, vec2 u) {
    vec2  R = iResolution.xy, U = u+u - R,
          l = 7.* length(U)/R;
    l.y < 7. ? O += mix( t(-1.), t(), min( fract(l) *R/14., 1. ) ).y : O;
} /*






// --- 231 by coyote
//                       -w , w : bug on OpenGL       k-- : not ok on Angle
#define t(k) smoothstep(-w.y,w.y, abs( fract( exp2(k+ceil(l)) * atan(U.y,U.x)/6.283 + iTime ) -.5 ) - .25 ) //
#define mainImage(O,u)                                                     \
    vec2  R = iResolution.xy, U = u+u - R,                                 \
          l = 7.* length(U)/R, w = 3.* exp2(l)/l / R;                      \
    l.y < 7. ? O += mix( t(-1.), t(), min( fract(l) *R/14., 1. ) ).y : O  /*






// --- 235 by coyote

#define t(l) smoothstep(-w,w, abs( fract( exp2(ceil(l)) * atan(U.y,U.x)/6.283 + iTime) -.5) -.25) //
#define mainImage(O,u)                                                     \
    vec2  R = iResolution.xy, U = u+u - R;                                 \
    float l = 7.* length(U) / R.y ,                                        \
          w = 3.* exp2(l)/l / R.y;                                         \
    l < 7. ? O += mix( t(l-1.), t(l), min( 1., fract(l) *R.y/14. ) ) : O  /*






// --- 243 chars

#define t(l) smoothstep(-w,w, abs( fract( exp2(l) * a + iTime ) -.5 ) - .25 ) //
#define mainImage(O,u)                                                         \
    vec2  R = iResolution.xy, U = u+u - R;                                     \
    float l = 7.* length(U) / R.y , a = atan(U.y,U.x) / 6.283,                 \
          L = ceil(l) , w = 3.* exp2(l)/l / R.y;                               \
    l < 7. ? O += mix( t(L-1.), t(L), min( 1., fract(l) *R.y/14. ) ) : O      /*

