// Fork of "Fourier Transform Schrodinger" by wyatt. https://shadertoy.com/view/ttcGzH
// 2020-03-28 22:24:07

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    Q = -.5+vec4(a.z,0.5*(a.w+a.z),a.w,1);
}

vec2 init (vec2 U) {
	return 1.+vec2(cos(U.x),sin(U.x))*exp(-.01*dot(U-0.5*R,U-0.5*R));
}
void mainImage( out vec4 Q, in vec2 U )
{
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) {
        if (iFrame < 10) Q.xy += init(vec2(U.x,i)).xy*e(-(w.y-pi)*i);
        else Q.xy += A(vec2(U.x,i)).zw*e(-(w.y-pi)*i);
        Q.zw += B(vec2(U.x,i)).zw*e(w.y*i)/R.y;
    }
	Q.zw *= mod(U.x+U.y,2.)*2.-1.;
    if (length(U-iMouse.xy)<1.&&iMouse.z>0.) Q.zw *= e(1.);
}

#define R (iResolution.xy)
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define pi 3.14159265359
mat2 e (float t) {
    float c = cos(t),s = sin(t);
	return mat2(c,-s,s,c);
}

void mainImage( out vec4 Q, in vec2 U )
{
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) {
        Q.xy += A(vec2(i,U.y)).xy*e(-(w.x-pi)*i);
        Q.zw += B(vec2(i,U.y)).xy*e(w.x*i)/R.x;
    }
    w -= pi;
    Q.xy *= e(length(w));
}
