void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float sampleCount=0.0;

    vec4 colorSamples=vec4(0.0);

    vec4 centerSample=texelFetch(iChannel0,ivec2(fragCoord),0);
    for(int y=-5;y<=0;y++)
    {
        for(int x=-5;x<=0;x++)
        {
            vec4 sample1=texelFetch(iChannel0,ivec2(fragCoord)+ivec2(x,y),0);
            vec4 sample2=texelFetch(iChannel0,ivec2(fragCoord)-ivec2(x,y),0);

            float dzdt1=sample1.z-centerSample.z;
            float dzdt2=centerSample.z-sample2.z;

            const float epsilon=0.1;
            float lowestdzdt=min(abs(dzdt1),abs(dzdt2));
			float weight1=lowestdzdt==abs(dzdt1)?1.0:0.0;
            float weight2=lowestdzdt==abs(dzdt2)?1.0:0.0;

            if(weight1==0.0)
            {
                weight1=abs(lowestdzdt-abs(dzdt1))<epsilon?1.0:0.0;
            }
            else if(weight2==0.0)
            {
                weight2=abs(lowestdzdt-abs(dzdt2))<epsilon?1.0:0.0;
            }

            colorSamples+=sample1*weight1+sample2*weight2;
            sampleCount+=weight1+weight2;
        }
    }

    // Output to screen
    fragColor = colorSamples/sampleCount;
}

float SDF(vec3 p)
{
    float s1=length(p)-0.1;
    float s2=length(p-vec3(-0.2,0.0,0.2))-0.1;
    return min(s1,s2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,0.0,10000.0);

    vec2 uv=fragCoord/iResolution.xy*2.0-1.0;

    vec3 dr=normalize(uv.x*vec3(1.0,0.0,0.0)+uv.y*vec3(0.0,1.0,0.0)+vec3(0.0,0.0,1.0));
    vec3 o=vec3(0.0,0.0,-0.2);
    float d=0.0;
    for(int i=0;i<64;i++)
    {
        vec3 p=o+dr*d;
        float s=SDF(o+dr*d);
        if(s<0.001)
        {
            vec2 uv=normalize(p.xz);
            vec4 color=texture(iChannel0,uv);

            fragColor=vec4(color.xyz*(p.z>0.1?10.0:1.0),p.z);
            break;
        }
        d+=s;
    }

}
