// Extremely primitive rand function https://stackoverflow.com/a/4275343/681026
float rand(vec2 co)
{
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

   	float val = uv.x;

    // Scale the randomness to range across zero, so we don't change the overall tone on average
    float val_with_noise = clamp(val + rand(uv)*noise_scale - noise_scale/2.0, 0.0, 1.0);

    float useVal = uv.y < 0.5 ? val : val_with_noise;


    float distFromCentre = 0.5 - uv.y;
    fragColor = abs(distFromCentre) < 0.001 ? vec4(1.0, 0.0, 0.0, 1.0) : vec4(useVal, useVal, useVal, 1.0);

    // Quantise to 8-bit so the original error is visible even if you're using a hdr screen.
    // I don't actually know if shadertoy uses floating point render targets, so I just put this
    // in just in case. Can't test since I don't have a HDR screen.
    fragColor = vec4(ivec4(fragColor*vec4(255.0, 255.0, 255.0, 255.0))) / vec4(255.0, 255.0, 255.0, 255.0);
}
