const float numOfBalls = 10.;										// number of balls
const float distanceTraveled = .8;
const float speed = 1.;												// speed of effect

float metaballs(vec2 uv, float time) {									
    float size = .5;												// base size
    const float startIndex = numOfBalls;
	const float endIndex = numOfBalls * 2.;

    for(float i = startIndex; i < endIndex; i++) {					// create x number of balls											
        float radius = distanceTraveled * sin(time + i * 2.);		// calculate radius
        vec2 ball = radius * vec2(sin(i), cos(i));					// ball position
		size += 1. / pow(i, distance(uv, ball));					// metaball calculation
    }
    return size;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    float time = iTime * speed;										// assigning time
    vec2 uv = (2. * fragCoord - R.xy) / R.y;						// center coordinates
	uv *= 3.; 														// zoom out    

    float blob = metaballs(uv, time);								// creating the blob
    blob = smoothstep(1., 1. + 50./R.y, blob);						// Anti Aliasing			

    vec3 col = vec3(.8, .2, .2);									// color of blob
    vec3 final = blob * col;										// combining the color with the effect
    fragColor = vec4(final, 1); 									// ouput
}
