// RANDOM 2D
// https://www.shadertoy.com/view/4dS3Wd
float hash(float n) { return fract(sin(n) * 1e4); }
float hash(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    return mix(hash(i), hash(i + 1.0), f);
}

#define COLOR1 vec3(41.0, 53.0, 58.0)
#define COLOR2 vec3(30.0, 38.0, 43.0)
#define COLOR3 vec3(23.0, 31.0, 34.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);

    uv.y *= 2.;
    uv.y -= 0.5;

    col = mix(COLOR1 / 255.0, col, step(noise((uv.x + iTime * 0.1) * 10.) * 0.2,uv.y - 0.7));
    col = mix(COLOR2 / 255.0, col, step(noise((uv.x + iTime * 0.05) * 13.) * 0.2,uv.y - 0.5));
    col = mix(COLOR3 / 255.0, col, step(noise((uv.x + iTime * -0.05) * 15.) * 0.2,uv.y - 0.3));
    col = mix(vec3(0.0), col, step(noise((uv.x + iTime * -0.1) * 18.) * 0.2,uv.y - 0.1));

    fragColor = vec4(col,1.0);
}
