#define tex(a) texture(iChannel0, a)


float kernel[9] = float[9](1./16., 1./8., 1./16., 1./8., 1./4., 1./8., 1./16., 1./8., 1./16.);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;



    if(fract(iTime/10.) > .5)
    {
    	vec2 pixelSize = 1./iResolution.xy;
    	pixelSize.x *= iResolution.x/iResolution.y;

    	vec3 total = vec3(.0);
    	for(int i = 0; i < 3; i++)
    	    for(int j = 0; j < 3; j++)
    	    {
    	        vec2 sampleUV = vec2(uv.x + float(i-1)*pixelSize.x, uv.y + float(j-1)*pixelSize.y);

    	        total += tex(sampleUV).xyz*kernel[j*3+i];
    	    }



    	vec3 blurIndicator = vec3(.0,1.0,.0)*smoothstep(.05, .045,distance(uv, vec2(.1)));


        fragColor = vec4(total+blurIndicator,1.0);
    } else 
    {
    	fragColor = tex(uv);
    }





}
