vec4 color = vec4(0.0, 0.0, 0.0, 0.0);
float numTilesX = 10.;
float numTilesY = 9.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // Setting the X and Y indicies individually.
    uv.x *= numTilesX;
    uv.y *= numTilesY;

    // Modding the UV coords by the the X and Y indexes of the matrix.
    float shouldShowColor = mod(floor(uv.x) + floor(uv.y), 2.);

    // By setting the r, g and b values to shouldShowColor,
    // white will be displayed when in an even column, 
    // and black when in an odd one.
    color.rgb = vec3(shouldShowColor);

    fragColor = color;
}
