void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( 2.* fragCoord - iResolution.xy) / iResolution.xy;  

    float s = sin(iTime);
    uv *= 1. + 3. * s * s;
    uv.x *= iResolution.x / iResolution.y;

    float x = uv.x, y = uv.y;

    float zRed = smoothstep(0.2, 0., abs(sin(x + iTime) + sin(y)));
    float zGreen = smoothstep(0.2, 0., abs(cos(x-iTime) + sin(y)));;
    float zBlue = smoothstep(0.2, 0., abs(sin(x) * sin(y + iTime)));;

    fragColor = vec4(zRed, zGreen, zBlue ,1);
}
