void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // get pixel information from uv location
    vec4 texColor = texture(iChannel0, uv);

    // works in this case but we don't want to invert alpha
    // texColor = vec4(1.0) - texColor;
    // set alpha back to 1.0
    // texColor.a = 1.0;

    // another way
    // texColor.rgb = vec3(1.0) - texColor.rgb;

    // long version
    texColor.r = 1.0 - texColor.r;
    texColor.g = 1.0 - texColor.g;
    texColor.b = 1.0 - texColor.b;

    // Output to screen
    fragColor = vec4(texColor);
}
