// variant of https://shadertoy.com/view/ws3yRN

#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) ) // https://www.shadertoy.com/view/llySRh

void mainImage( out vec4 O, vec2 U )
{	
    O = hue( T(U)); 
 // O = hue(  texture( iChannel0, U/iResolution.xy , 2. )) ; 
}

#define hash(p)    fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453) // https://www.shadertoy.com/view/llySRh
#define hash2(p)   fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)

void mainImage( out vec4 O, vec2 U )
{
    O = T( U );
    for (int i=0; i<9; i+= i==3 ? 2 : 1 ) {
        vec2 D = vec2( i%3-1, i/3-1);
        if ( hash(U+float(i)/11.+iTime) < .1 ) O =  max(O, T(U+D) );
               //  +D
    }

    if (iFrame%1==0) O += .1 * vec4( length(U-iResolution.xy*hash2(U+iTime)) < 2. );
 // if (iFrame%1==0) O += hash(U+iTime-.1) * vec4( length(U-iResolution.xy*hash2(U+iTime)) < 2. );

}
