#define C(r) clamp( 1. - R.y/2.* ( length(U) - r ) , 0.,1. )           //
void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y; U.y += .6;
    O *=  C(.1);                U.y -= .2;
    for(float i=1.; i<7.; i+= i==1. ? .1 : 1. )
        U.x = abs(U.x),
        O += (1.-O.a) * C(.5) / vec4(i,i,i,1),
        U = (U/.6-.9) * mat2(cos(.8+.3*sin(6.*iTime)+vec4(0,33,11,0))),
        R *= .6;}
