void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec2 uv = (fragCoord - .5 * iResolution.xy) /iResolution.y;uv.y = abs(uv.y);
    vec2 st = vec2(atan(uv.x * (sin(iTime * cos(iTime + sin(iTime  * .25)))), uv.y), length(uv));
 uv.x = abs(uv.x * 12.
           );   
 uv = st; 

    float x = uv.x * cos(uv.y + 1.);
    float m = max(fract(x), fract(sin(uv.y * iTime * 5.) * cos(iTime * uv.x * uv.y)));
    float ma = max(fract(m * x), fract(sin(uv.y * 5.) * sin(m * uv.y)));
   	vec3 col = st.xxx / (12.1417 * 2.) + .5;
	col = vec3(m * length(st * sin(iTime)), fract(ma * m), m + uv.y);
    vec3 ttreh= vec3(1.25) * col * abs(uv.y * uv.x);
    fragColor = vec4(sqrt(ttreh) *ttreh,1.0);
}
