vec2 zoomUv(vec2 uv, float zoom) {
    vec2 uv1 = uv;
    uv1 += .5;
    uv1 += zoom/2.-1.;
    uv1 /= zoom;
    return uv1;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 disUV = uv*2.-1.;
    float distortion = length(disUV);
    distortion = min(distortion, 1.0);
    distortion *= 0.01;

    float disMult = (iMouse.x/iResolution.x)*3.;

    vec2 uvR = zoomUv(uv, distortion*disMult+1.);
    vec2 uvG = zoomUv(uv, distortion*disMult*2.+1.);
    vec2 uvB = zoomUv(uv, distortion*disMult*3.+1.);

    float r = vec4(texture(iChannel0, uvR)).r;
    float g = vec4(texture(iChannel0, uvG)).g;
    float b = vec4(texture(iChannel0, uvB)).b;

    vec4 tex = texture(iChannel0, uv);

    fragColor = vec4(r, g, b, 1.0);
}
