void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = texture(iChannel0,uv);
    col += texture(iChannel0,uv+vec2( 1.  /iResolution.x,0.))/2.;
    col += texture(iChannel0,uv+vec2(-1.  /iResolution.x,0.))/2.;
    col += texture(iChannel0,uv+vec2(0., 1.  /iResolution.y))/2.;
    col += texture(iChannel0,uv+vec2(0.,-1.  /iResolution.y))/2.;
    col += texture(iChannel0,uv+vec2(-1.,-1.)/iResolution.xy)/4.;
    col += texture(iChannel0,uv+vec2(-1., 1.)/iResolution.xy)/4.;
    col += texture(iChannel0,uv+vec2( 1.,-1.)/iResolution.xy)/4.;
    col += texture(iChannel0,uv+vec2( 1., 1.)/iResolution.xy)/4.;
    col/=4.;
    col = col*(col)+(1.-col)*texture(iChannel0,uv);
    fragColor = vec4(col);
}

#define lz sin(iTime/3.)*3.+6.

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec3 uv = normalize(vec3((fragCoord-iResolution.xy/2.)/iResolution.y,1));
    vec3 uv1 = uv/uv.x;
    vec3 uv2 = uv/uv.y;
    float rlen = min(dot(uv1,uv1),dot(uv2,uv2));
    vec2 pos = rlen==dot(uv1,uv1)?uv1.yz:uv2.xz;
    pos/=2.;
    pos.y+=sign(uv.x+uv.y)*iTime/5.;
    rlen-=lz;
    vec3 col = vec3(pow((rlen*rlen+1.)*.5,-15./22.))*texture(iChannel0,pos).rgb;
    fragColor = vec4(dot(uv.xy,uv.xy)<0.01/(lz)?vec3(1.,1.,1.):col,1.);
}
