float march_shadow(vec2 pos)
{
	float MAX_DIST = 5./iResolution.x;
    const float STEP_SIZE = 0.0001;
    const vec2 dir = normalize(vec2(0.2, 0.2));

    float dist = 0.;
    while(dist < MAX_DIST)
    {
        vec2 p = pos+dir*dist;
        float t = texture(iChannel1, p).x;
		if(t >= 1.)
        {

			break;

        }
        dist += STEP_SIZE;

    }

    return (dist/MAX_DIST);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = fragCoord+iTime*20.;
    vec2 uv = pos/iResolution.xy;


    int cell = int(texture(iChannel1, uv*SCALE).x);
    vec3 col = vec3(.8);
    if(cell >= 1)
    {
		col = mix(vec3(0.95), vec3(.7, 0.1, 0.2)*1.4, 1.-min(float(cell)/6., 1.));
    }

    float grid = step(1.-GRID_SIZE, fract((pos.x-0.5)*SCALE));
    grid += step(1.-GRID_SIZE, fract((pos.y-0.5)*SCALE));
   	grid = min(grid, 1.);
    if(grid == 1.)
    {
    	col = vec3(0.9);
    }
    if(cell <= 0)
    {
		float shadow = march_shadow(uv*SCALE);
    	col *= min(shadow+0.3, 1.);
    }


    fragColor = vec4(col,1.0);
}

float SCALE = .1;
float SPEED = 4.;
float GRID_SIZE = 0.15;

int BORN[] = int[](3);
int SURVIVE[] = int[](2,3);

float rand2(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 offsets[] = vec2[](
    vec2(0, 1),
    vec2(1, 0),
    vec2(0, -1),
    vec2(-1, 0),
    vec2(1, 1),
    vec2(-1, 1),
    vec2(-1, -1),
    vec2(1, -1));


int get_total(vec2 pos)
{
	int tot = 0;
    for(int i = 0; i < 8; i++)
    {
		tot += min(int(texture(iChannel1, (pos+offsets[i])/iResolution.xy).x), 1);
    }
    return tot;
}

int get_new_state(int current, int total)
{
    int new = 0;

    if(current <= 0)
    {
		for(int i = 0; i < BORN.length(); i++)
        {
			if(total == BORN[i])
            {
             	new = 1;   
            }
        }
    }
    else
    {
		for(int i = 0; i < SURVIVE.length(); i++)
        {
			if(total == SURVIVE[i])
            {
             	new = current + 1;   
            }
        }
    }
	return new;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    if(iFrame < 5)
    {
		fragColor = min(floor(vec4(rand2(floor(fragCoord))+0.2)), 1.);
    }
    else
    {
        if(mod(float(iFrame), SPEED) == 0.)
        {
			fragColor = vec4(get_new_state(int(texture(iChannel1, uv)), get_total(fragCoord)));
        }
        else
        {
			fragColor = texture(iChannel1, uv);
        }
    }
}
