void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / 64.0;
    float time = clamp(mod(iTime * 2.0, 7.28) - 0.5, 0.0, 6.28);

    uv.x *= 1.5 + 0.5 * sin(time + 4.71);
    uv.x -= 2.0 * (max(0.0, mod(time, 6.28) - 3.14) / 3.14);

    fragColor = vec4(0.5);
    if (fragCoord.x < 512.0 && fragCoord.y < 512.0) {
        fragColor = vec4(mod(floor(uv.x) + floor(uv.y), 2.0));
    }
}
