/* 
    The Julia set is the subset of the complex plane given by the points 
    whose nth iteration of the function f_c (z) = z * z + c has a modulus 
    of at most two for all natural n.
*/

vec2 multiply(vec2 a,vec2 b){
    if(a.x==0.){
        if(b.x==0.)
            return vec2(-a.y*b.y,0.);
        else
            return vec2(-a.y*b.y*sign(b.x),a.y*abs(b.x));
    }
    else{
        if(b.x==0.)
            return vec2(-a.y*b.y*sign(a.x),b.y*abs(a.x));
        else{
            return vec2	(a.x*b.x-a.y*b.y*sign(a.x*b.x),abs(a.x)*b.y+a.y*abs(b.x));
        }
    }
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 z0 = 5.*(fragCoord/iResolution.x-vec2(.5,.27));
    vec2 col;
    vec2 c = cos(iTime)*vec2(cos(iTime/2.),sin(iTime/2.)); 
    for(int i = 0; i < 500;i++){
        vec2 z = multiply(z0,z0)+c;
        float mq = dot(z,z);
        if( mq > 4.){
            col = vec2(float(i)/20.,0.);
            break;
        } else {
            z0 = z;
        }
        col =  vec2(mq/2.,mq/2.);
    }
    fragColor = vec4(col,0.,1.);
}
