#define pi 3.14159265359
float ssin(float x, float s)//aliased sin
{
    return cos(floor(x*s)*pi/s);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = 4.*(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
    float t = iTime+1002.56+m.x*.01;
    float n = 277.;
    float s = 0.;
    for(float i = .5; i < n; ++i)
    {
        vec2 a = sin(t*i+vec2(0.,pi*.5));
        s += ssin(dot(u,a)+t*.001*i,1.);
    }
    s = s/sqrt(n)+4.;
    fragColor = .5+.5*cos(s*.5+vec4(.7,.1,.0,.0)
                          + pi*2.*m.y);
}
