// Fork of https://shadertoy.com/view/wsd3R7
// funny: ceil(U) instead of floor create artifacts at some resolutions


void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = 3.* (u+u-R)/R.y, C = mod(floor(U),2.),
         S = C.x == C.y ? R/R : -R/R,
         B = 3.1*(fract(U)-.5) * mat2(cos(vec4(0,33,11,0) +S.x*iTime)),
         A = abs(B), D = .9*A-.5*A.x;
    O += max( .5 - ( min(abs(D),A.x) -.2 )/30.*R, 0.).y;
    O[ D.y<0. ? 0 : B.x*B.y*S.x > 0. ? 1 : 2 ] ++;
}
