//Hash21 from https://www.youtube.com/watch?v=2R7h76GoIJM
float Hash21(vec2 p) {
    p = fract(p*vec2(234.34, 435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv.x = uv.x*2.;

    float angle = 0.78539;
	vec2 uvd = uv;
    uv.x = uvd.x*cos(angle) - uvd.y*sin(angle);
	uv.y = uvd.x*sin(angle) + uvd.y*cos(angle);





    uv *= 10.;
	vec2 id = floor(uv);
    float n = Hash21(id*floor(iTime*0.88+3000.));

	uv = fract(uv);

	float mask = 0.;

    mask = n;
    vec3 col = vec3(mask*cos(mask*floor(iTime)),mask*sin(mask*floor(iTime)),0.2);



    fragColor = vec4(col,1.0);
}
