void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    uv *= 10.;

    uv += iTime;
    vec2 otherUV = uv;

    uv = vec2(sin(uv.x), cos(uv.y));


    float combi = fract(uv.x+uv.y);
    float diffA = abs(dFdx(fract(uv.x+uv.y+iTime)))+abs(dFdy(fract(uv.x+uv.y)));

    uv = otherUV;
    float diffB = abs(dFdx(fract(uv.x+uv.y)))+abs(dFdy(fract(uv.x+uv.y-iTime)));

    //diff = clamp(diff,0.,1.);

    vec4 Acol = vec4(0,0,0,1);
    vec4 Bcol = vec4(0,0.7,0,1);
    vec4 Ccol = vec4(.4,.6,0,1);

	vec4 outA = mix(Acol,Bcol,diffA*4.);
    vec4 outB = mix(Acol,Ccol,diffB);


    // Output to screen
    fragColor = outA+outB;
}
