float circle(vec2 uv, vec2 center, float radius) {
	return smoothstep(radius, radius-0.01, length(uv - center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 RES = iResolution.xy;
    float RATIO = RES.x / RES.y;

    vec2 uv = fragCoord/RES; // [0, 1]
    uv = uv - 0.5; // [-0.5, 0.5]
    uv.x = uv.x * RATIO;

    vec3 col1 = vec3(1.0, 0.0, 0.0);
    vec3 col2 = vec3(0.0, 1.0, 0.0);
    vec3 col3 = vec3(0.0, 0.0, 1.0);

    vec3 mask = vec3(0.0);
    float radius = 0.2;
    float spread = sin(iTime) + 2.0 * 0.5;

    mask = vec3(0.15);
    mask += vec3(circle(uv, vec2(0.0, 0.05) * spread, radius)) * col1;
    mask += vec3(circle(uv, vec2(0.0577, -0.05) * spread, radius)) * col2;
    mask += vec3(circle(uv, vec2(-0.0577, -0.05) * spread, radius)) * col3;

    fragColor = vec4(mask,1.0);
}
